mapper.select:
select * from (select rownum R, P.* from (select * from pay where id=#{id} and
id
like '%' || #{search} || '%' order by pay_date desc) P) where R between #{startRow} and #{lastRow}

select * from (select rownum R, P.* from (select * from pay where category=0 and
id
like '%' || #{search} || '%' order by num desc) P) where R between #{startRow} and #{lastRow}
